<?php

namespace QuidditchBundle\Repository;

/**
 * EquipeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EquipeRepository extends \Doctrine\ORM\EntityRepository
{
    public function findByEquipeAttributs($exp, $age, $fatigue)
    {
        return $this->createQueryBuilder('e')
            ->select('e, COUNT(e.joueur) as joueur')
            ->join('e.joueurs', 'j')
            ->groupBy('e.joueurs, e.id')
            ->setParameter('exp', $exp)
            ->setParameter('age', $age)
            ->andWhere('h.city=:city')
            ->setParameter('fatigue', $fatigue)
            ->orderBy('nom', 'DESC')
            ->getQuery()->getResult();
    }
}
